package net.reduck.chat.server.io;

import net.reduck.chat.server.utils.Ciphers;
import org.junit.jupiter.api.Test;

import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.IOException;

/**
 * @author Reduck
 * @since 2023/1/4 10:22
 */
public class ChatMessageReaderTest {
    private byte[] keys = "1234567812345678".getBytes();
    private byte[] end = new byte[] {0x0, 0x0, 0x0, 0x0, 0x0, 0x0, 0x0, 0x0};
    private String data = "";

    @Test
    public void testRead() throws IOException {
        ByteArrayOutputStream os = new ByteArrayOutputStream();
        os.write(Ciphers.encrypt(data.getBytes(), keys));
        os.write(end);
        ChatMessageReader reader = new ChatMessageReader(new ByteArrayInputStream(os.toByteArray()));
        byte[] message = reader.readMessage();
        System.out.println(new String(Ciphers.decrypt(message, keys)));
    }
}
